import React, { useEffect, useMemo, useRef, useState } from "react";
import "./Paper.css";

const DEFAULT_PDF =
  "https://contactfunctionstor123.blob.core.windows.net/portfolio-resumes/TEST%20DOCUMENT%20FOR%20WEB%20DEVELOPMENT.pdf";

/**
 * Advanced iframe PDF viewer.
 * Keeps <iframe>, but adds: toolbar, page jump, fit-width, fullscreen, copy link, skeleton.
 */
const Paper = ({
  src = DEFAULT_PDF,
  title = "Research Paper",
  height = "82vh",          // you can pass "700px" if you prefer
}) => {
  const containerRef = useRef(null);
  const [page, setPage] = useState(1);
  const [zoomMode, setZoomMode] = useState("auto"); // "auto" | "width"
  const [loaded, setLoaded] = useState(false);
  const [copied, setCopied] = useState(false);
  const [timedOut, setTimedOut] = useState(false);

  // Build iframe URL with PDF hash params (widely supported by native viewers)
  const frameSrc = useMemo(() => {
    const base = src.split("#")[0];
    const hash = [];
    if (page && page > 1) hash.push(`page=${page}`);
    if (zoomMode === "width") hash.push("zoom=page-width"); // Chrome/Edge/Firefox support
    return hash.length ? `${base}#${hash.join("&")}` : base;
  }, [src, page, zoomMode]);

  // Loading timeout (in case remote viewer is slow)
  useEffect(() => {
    setLoaded(false);
    setTimedOut(false);
    const t = setTimeout(() => setTimedOut(true), 8000);
    return () => clearTimeout(t);
  }, [frameSrc]);

  // Fullscreen
  const goFullscreen = () => {
    const el = containerRef.current;
    if (!el) return;
    if (el.requestFullscreen) el.requestFullscreen();
    else if (el.webkitRequestFullscreen) el.webkitRequestFullscreen();
    else if (el.msRequestFullscreen) el.msRequestFullscreen();
  };

  // Copy shareable link (with current page/zoom in the hash)
  const copyLink = async () => {
    try {
      await navigator.clipboard.writeText(frameSrc);
      setCopied(true);
      setTimeout(() => setCopied(false), 1200);
    } catch (e) {
      // no-op
    }
  };

  const stepPage = (delta) =>
    setPage((p) => Math.max(1, (Number.isFinite(p) ? p : 1) + delta));

  const onPageInput = (e) => {
    const v = parseInt(e.target.value, 10);
    if (!Number.isNaN(v) && v > 0) setPage(v);
  };

  return (
    <div
      className="paper-shell"
      ref={containerRef}
      style={{ ["--paper-h"]: height }}
    >
      <div className="paper-toolbar">
        <div className="paper-left">
          <span className="paper-title" title={title}>{title}</span>
        </div>

        <div className="paper-right">
          {/* Page controls (hash-based) */}
          <div className="paper-pages">
            <button
              className="btn btn-mini"
              aria-label="Previous page"
              onClick={() => stepPage(-1)}
            >
              ‹
            </button>
            <input
              className="page-input"
              inputMode="numeric"
              pattern="[0-9]*"
              value={page}
              onChange={onPageInput}
              aria-label="Page number"
              title="Jump to page"
            />
            <button
              className="btn btn-mini"
              aria-label="Next page"
              onClick={() => stepPage(1)}
            >
              ›
            </button>
          </div>

          {/* Fit mode */}
          <div className="paper-zoom">
            <button
              className={`btn btn-mini ${zoomMode === "auto" ? "active" : ""}`}
              onClick={() => setZoomMode("auto")}
              title="Auto fit"
            >
              Fit
            </button>
            <button
              className={`btn btn-mini ${zoomMode === "width" ? "active" : ""}`}
              onClick={() => setZoomMode("width")}
              title="Fit to width"
            >
              Width
            </button>
          </div>

          {/* Actions */}
          <a className="btn btn-mini" href={src} target="_blank" rel="noreferrer">
            Open
          </a>
          <a className="btn btn-mini" href={src} download>
            Download
          </a>
          <button className="btn btn-mini" onClick={goFullscreen}>
            Fullscreen
          </button>
          <button className="btn btn-mini" onClick={copyLink}>
            {copied ? "Copied" : "Copy Link"}
          </button>
        </div>
      </div>

      <div className="paper-framewrap">
        {!loaded && (
          <div className="paper-skeleton" aria-hidden="true">
            <div className="shimmer" />
          </div>
        )}

        <iframe
          title={title}
          src={frameSrc}
          loading="lazy"
          onLoad={() => setLoaded(true)}
          className="paper-iframe"
        />
      </div>

      <div className="paper-footer">
        <span className="hint">
          Tip: Use your browser’s PDF controls for zoom, search (<kbd>Ctrl/⌘</kbd> + <kbd>F</kbd>), and print.
        </span>
        {timedOut && !loaded && (
          <span className="fallback">
            Having trouble?{" "}
            <a href={src} target="_blank" rel="noreferrer">Open in a new tab</a>.
          </span>
        )}
      </div>
    </div>
  );
};

export default Paper;
