// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import Header from "./components/Header";
import Footer from "./components/Footer";

import LandingPage from "./pages/LandingPage";
import ProjectDescription from "./pages/ProjectDescription";
import ResearchPage from "./pages/ResearchPage";
import LoginPage from "./pages/LoginPage";
import DataPage from "./pages/DataPage";
import CreateAccount from "./pages/CreateAccount"; // ← NEW

import "./styles/AppShell.css";

// (Optional) keep your login guard for /dataset if you already added one
const isAuthed = () => localStorage.getItem("auth") === "true";
const RequireLogin = ({ children }) => (isAuthed() ? children : <Navigate to="/login" replace />);

function App() {
  return (
    <Router>
      <div className="app-shell">
        <Header />

        <main className="app-main" role="main">
          <Routes>
            <Route path="/" element={<LandingPage />} />
            <Route path="/description" element={<ProjectDescription />} />
            <Route path="/research" element={<ResearchPage />} />
            <Route path="/login" element={<LoginPage />} />

            {/* NEW: create-account route */}
            <Route path="/create-account" element={<CreateAccount />} />

            {/* dataset still guarded (if you use the guard) */}
            <Route
              path="/dataset"
              element={
                <RequireLogin>
                  <DataPage />
                </RequireLogin>
              }
            />

            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
