// src/pages/ResearchPaper.jsx
import React, { useState, useEffect } from "react";
import Paper from "../components/Paper";
import SkeletonInfo from "../components/SkeletonInfo"; // ✅ skeleton loader component
import "../styles/ResearchPage.css";

const ResearchPaper = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate a loading delay (e.g., fetching doc info)
    const timer = setTimeout(() => setLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <main className="research-page">
      {/* Main layout */}
      <div className="research-content">
        {/* Left: PDF viewer */}
        <section className="paper-section">
          <Paper />
        </section>

        {/* Right: Info panel */}
        {loading ? (
          <SkeletonInfo /> // ✅ show skeleton while loading
        ) : (
          <aside className="info-section">
            <h2>About This Document</h2>
            <p>
              This research paper is part of the Toyota MicroDoppler Project.
              You can read it directly on the left or download it for offline use.
            </p>
            <p>
              The viewer supports continuous scrolling. On smaller devices it
              auto-resizes to fit the screen.
            </p>
          </aside>
        )}
      </div>
    </main>
  );
};

export default ResearchPaper;
