// src/components/LandingPage.jsx
import React, { useEffect, useState } from "react";
import "../styles/LandingPage.css";

import landingBg from "../assets/images/LANDING1.png";
import titleBg from "../assets/images/Title_Background.jpg";
import purdueLogo from "../assets/images/Purdue.png";
import tasiLogo from "../assets/images/TASI.png";
import toyotaLogo from "../assets/images/Toyota.png";
import csrcLogo from "../assets/images/CSRC.png";
import osuLogo from "../assets/images/OSU.png";
import eslLogo from "../assets/images/ESL.png";

const LandingPage = () => {
  const [showContent, setShowContent] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowContent(true);
    }, 2000); // ⏱ 2 seconds delay

    return () => clearTimeout(timer);
  }, []);

  return (
    <div
      className="landing-page"
      style={{ backgroundImage: `url(${landingBg})` }}
    >
      {showContent && (
        <>
          {/* Title Section */}
          <div
            className="title-section"
            style={{ backgroundImage: `url(${titleBg})` }}
          >
            <h1>Purdue – OSU – Toyota</h1>
            <h2>Radar MicroDoppler Dataset</h2>
          </div>

          {/* Logos Section */}
          <div className="brand-logos">
            {/* Row 1 */}
            <div className="logo-card">
              <img src={purdueLogo} alt="Purdue University" loading="lazy" />
            </div>
            <div className="logo-card">
              <img src={toyotaLogo} alt="Toyota Research & Development" loading="lazy" />
            </div>
            <div className="logo-card">
              <img src={osuLogo} alt="The Ohio State University" loading="lazy" />
            </div>

            {/* Row 2 */}
            <div className="logo-card">
              <img src={tasiLogo} alt="TASI" loading="lazy" />
            </div>
            <div className="logo-card">
              <img src={csrcLogo} alt="Toyota CSRC" loading="lazy" />
            </div>
            <div className="logo-card">
              <img src={eslLogo} alt="ElectroScience Laboratory" loading="lazy" />
            </div>
          </div>

          {/* Footer */}
          <footer className="footer">
            <p>Toyota MicroDoppler Project © 2025</p>
          </footer>
        </>
      )}
    </div>
  );
};

export default LandingPage;
