// src/App.jsx
import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useLocation,
} from "react-router-dom";

import Header from "./components/Header";
import Footer from "./components/Footer";

import LandingPage from "./pages/LandingPage";
import ProjectDescription from "./pages/ProjectDescription";
import ResearchPage from "./pages/ResearchPage";
import LoginPage from "./pages/LoginPage";
import DataPage from "./pages/DataPage";

// App-level shell styles for sticky footer
import "./styles/AppShell.css";

/* ---------------------------
   Simple auth helper
   (replace with real auth later)
---------------------------- */
const useAuth = () => localStorage.getItem("isAuthed") === "true";

/* Route guard: if not authed, go to /login and remember where user came from */
function RequireAuth({ children }) {
  const location = useLocation();
  const authed = useAuth();
  if (!authed) {
    return <Navigate to="/login" replace state={{ from: location }} />;
  }
  return children;
}

function AppShell() {
  return (
    <div className="app-shell">
      {/* Global Header always visible */}
      <Header />

      {/* Route-based page rendering */}
      <main className="app-main" role="main">
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/description" element={<ProjectDescription />} />
          <Route path="/research" element={<ResearchPage />} />

          {/* Public login route */}
          <Route path="/login" element={<LoginPage />} />

          {/* /dataset is protected: unauthenticated users are sent to /login */}
          <Route
            path="/dataset"
            element={
              <RequireAuth>
                <DataPage />
              </RequireAuth>
            }
          />

          {/* Fallback: redirect unknown routes to home */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </main>

      {/* Global Footer always visible */}
      <Footer />
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <AppShell />
    </Router>
  );
}
