// src/pages/DataPage.jsx
import React, { useMemo, useState } from "react";
import "../styles/DataPage.css";

/* ---------- Compact UI helpers (no width cap here) ---------- */
const smallFont = { fontSize: "0.82rem", lineHeight: 1.2 };
const smallTitle = { fontSize: "1rem", margin: 0, lineHeight: 1.2 };
const smallH2 = { fontSize: "0.95rem", margin: "0 0 8px", lineHeight: 1.2 };
const smallBtn = {
  padding: "6px 10px",
  fontSize: "0.82rem",
  borderRadius: 8,
  lineHeight: 1.2,
};
const smallInputStyle = {
  fontSize: "0.82rem",
  padding: "6px 8px",
  height: 30,
};

const SmallInput = (props) => (
  <input {...props} style={{ ...smallInputStyle, ...(props.style || {}) }} />
);
const SmallSelect = (props) => (
  <select {...props} style={{ ...smallInputStyle, ...(props.style || {}) }} />
);

const VRU_OPTIONS = [
  { key: "ped_arm", label: "Pedestrian with Arm Swing" },
  { key: "ped_no_arm", label: "Pedestrian Without Arm Swing" },
  { key: "bike_pedal", label: "Bicyclist With Pedaling" },
  { key: "bike_no_pedal", label: "Bicyclist Without Pedaling" },
  { key: "escooter", label: "E-Scooter" },
  { key: "wheelchair", label: "Wheelchair" },
  { key: "bird_escooter", label: "Bird E-Scooter" },
  { key: "lime_escooter", label: "Lime E-Scooter" },
];

const INIT_VRU_PREFS = VRU_OPTIONS.reduce((acc, o) => {
  acc[o.key] = "ignore"; // "ignore" | "must" | "mustNot"
  return acc;
}, {});

const DEFAULTS = {
  radarMotion: "Stationary",
  environment: "Controlled",
  numVRUs: "",
  pathType: "Straight", // Straight | Curved
  slope: "0",
  turn: "Right Turn by 90 Degrees",
  gait: "Paced Walking",
  minInterval: "0.00",
  minTrajLen: "0.00",
  ranges: {
    vruRange: { min: "0", max: "50" },
    azimuth: { min: "-45", max: "40" },
    velocity: { min: "0", max: "8" },
    rcs: { min: "-25", max: "-5" },
    snr: { min: "5", max: "30" },
  },
};

export default function DataPage() {
  const [radarMotion, setRadarMotion] = useState(DEFAULTS.radarMotion);
  const [environment, setEnvironment] = useState(DEFAULTS.environment);
  const [numVRUs, setNumVRUs] = useState(DEFAULTS.numVRUs);
  const [pathType, setPathType] = useState(DEFAULTS.pathType);
  const [slope, setSlope] = useState(DEFAULTS.slope);
  const [turn, setTurn] = useState(DEFAULTS.turn);
  const [vruPrefs, setVruPrefs] = useState({ ...INIT_VRU_PREFS });
  const [gait, setGait] = useState(DEFAULTS.gait);
  const [minInterval, setMinInterval] = useState(DEFAULTS.minInterval);
  const [minTrajLen, setMinTrajLen] = useState(DEFAULTS.minTrajLen);
  const [ranges, setRanges] = useState({ ...DEFAULTS.ranges });

  const showControlledOnly = environment === "Controlled";
  const showStraightOnly = showControlledOnly && pathType === "Straight";
  const showCurvedOnly = showControlledOnly && pathType === "Curved";
  const showGait = useMemo(
    () => vruPrefs["ped_arm"] === "must" || vruPrefs["ped_no_arm"] === "must",
    [vruPrefs]
  );

  function setVruPref(key, next) {
    setVruPrefs((prev) => {
      const current = prev[key];
      const value = current === next ? "ignore" : next; // toggle off
      return { ...prev, [key]: value };
    });
  }

  function resetAll() {
    setRadarMotion(DEFAULTS.radarMotion);
    setEnvironment(DEFAULTS.environment);
    setNumVRUs(DEFAULTS.numVRUs);
    setPathType(DEFAULTS.pathType);
    setSlope(DEFAULTS.slope);
    setTurn(DEFAULTS.turn);
    setVruPrefs({ ...INIT_VRU_PREFS });
    setGait(DEFAULTS.gait);
    setMinInterval(DEFAULTS.minInterval);
    setMinTrajLen(DEFAULTS.minTrajLen);
    setRanges({ ...DEFAULTS.ranges });
  }

  function applyFilters(e) {
    e?.preventDefault?.();
    const payload = {
      radarMotion,
      environment,
      numVRUs: numVRUs ? parseInt(numVRUs, 10) : null,
      pathType: showControlledOnly ? pathType : null,
      slope: showStraightOnly ? parseInt(slope, 10) : null,
      turn: showCurvedOnly ? turn : null,
      vruPreferences: vruPrefs,
      gait: showGait ? gait : null,
      minInterval: parseFloat(minInterval),
      minTrajectoryLength: showControlledOnly ? parseFloat(minTrajLen) : null,
      ranges: { ...ranges },
    };
    console.log("Apply Filters ->", payload);
  }

  return (
    <div className="data-page compact">
      <main className="container">
        <div className="page-title" style={{ marginBottom: 8 }}>
          <h1 style={smallTitle}>VRU Radar Micro-Doppler Database Tool</h1>
        </div>

        {/* Main grid */}
        <section
          className="grid"
          style={{ gap: 12, gridTemplateColumns: "1.6fr 1fr" }}
        >
          {/* LEFT: Scenario Filters */}
          <form className="card left" onSubmit={applyFilters} style={{ padding: 12 }}>
            <h2 style={smallH2}>Filters</h2>

            <div className="field" style={{ gap: 4, marginBottom: 8 }}>
              <label style={smallFont}>1. Radar Motion Type</label>
              <SmallSelect value={radarMotion} onChange={(e) => setRadarMotion(e.target.value)}>
                <option>Stationary</option>
                <option>Moving</option>
              </SmallSelect>
            </div>

            <div className="field" style={{ gap: 4, marginBottom: 8 }}>
              <label style={smallFont}>2. Data Collection Environment</label>
              <SmallSelect value={environment} onChange={(e) => setEnvironment(e.target.value)}>
                <option>Controlled</option>
                <option>Naturalistic</option>
              </SmallSelect>
            </div>

            <div className="field" style={{ gap: 4, marginBottom: 8 }}>
              <label style={smallFont}>3. Number of VRUs</label>
              <SmallInput
                type="number"
                inputMode="numeric"
                min="1"
                step="1"
                placeholder="Enter integer > 0"
                value={numVRUs}
                onChange={(e) => setNumVRUs(e.target.value)}
              />
            </div>

            {showControlledOnly && (
              <div className="field" style={{ gap: 4, marginBottom: 8 }}>
                <label style={smallFont}>4. Trajectory Path Type (Only if Controlled)</label>
                <SmallSelect value={pathType} onChange={(e) => setPathType(e.target.value)}>
                  <option value="Straight">Straight</option>
                  <option value="Curved">Curved</option>
                </SmallSelect>
              </div>
            )}

            {showStraightOnly && (
              <div className="field" style={{ gap: 4, marginBottom: 8 }}>
                <label style={smallFont}>5. Trajectory Path Slope (Straight path only)</label>
                <SmallSelect value={slope} onChange={(e) => setSlope(e.target.value)}>
                  {["-45", "0", "45", "90", "135", "180", "225", "270"].map((deg) => (
                    <option key={deg} value={deg}>{deg}</option>
                  ))}
                </SmallSelect>
              </div>
            )}

            {showCurvedOnly && (
              <div className="field" style={{ gap: 4, marginBottom: 8 }}>
                <label style={smallFont}>6. Trajectory Turn Options (Curved path only)</label>
                <SmallSelect value={turn} onChange={(e) => setTurn(e.target.value)}>
                  <option>Right Turn by 90 Degrees</option>
                  <option>Right Turn by 45 Degrees</option>
                </SmallSelect>
              </div>
            )}

            <div className="field" style={{ gap: 4, marginBottom: 8 }}>
              <label style={smallFont}>7. VRU Types</label>
              <div className="vru-grid" style={{ gap: 8 }}>
                {VRU_OPTIONS.map((o) => {
                  const value = vruPrefs[o.key];
                  const isMust = value === "must";
                  const isMustNot = value === "mustNot";
                  return (
                    <div key={o.key} className="vru-card" style={{ padding: 8 }}>
                      <div className="vru-title" style={{ fontSize: "0.9rem", marginBottom: 4 }}>
                        {o.label}
                      </div>
                      <div className="vru-controls" style={{ gap: 6 }}>
                        <label className={`toggle ${isMust ? "on" : ""}`} style={smallFont}>
                          <input
                            type="checkbox"
                            checked={isMust}
                            onChange={() => setVruPref(o.key, "must")}
                          />
                          Must contain
                        </label>
                        <label className={`toggle ${isMustNot ? "on" : ""}`} style={smallFont}>
                          <input
                            type="checkbox"
                            checked={isMustNot}
                            onChange={() => setVruPref(o.key, "mustNot")}
                          />
                          Must not contain
                        </label>
                      </div>
                      {value === "ignore" && (
                        <div className="vru-subtle" style={{ fontSize: "0.78rem" }}>
                          No preference
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>

            {showGait && (
              <div className="field" style={{ gap: 4, marginBottom: 8 }}>
                <label style={smallFont}>8. Pedestrian Gait Type (Pedestrian selected)</label>
                <SmallSelect value={gait} onChange={(e) => setGait(e.target.value)}>
                  <option>Paced Walking</option>
                  <option>Natural Walking</option>
                  <option>Jogging</option>
                  <option>Irregular Walking</option>
                </SmallSelect>
              </div>
            )}
          </form>

          {/* RIGHT: VRU Data Filters */}
          <aside className="card right" style={{ padding: 12 }}>
            <h2 style={smallH2}>VRU Data Filters</h2>

            <RangePair
              title="1. VRU Range (m)"
              value={ranges.vruRange}
              onChange={(min, max) => setRanges((r) => ({ ...r, vruRange: { min, max } }))}
            />
            <RangePair
              title="2. VRU Azimuth Angle (Deg)"
              value={ranges.azimuth}
              onChange={(min, max) => setRanges((r) => ({ ...r, azimuth: { min, max } }))}
            />
            <RangePair
              title="3. VRU Constituent Point Velocity (m/s)"
              value={ranges.velocity}
              onChange={(min, max) => setRanges((r) => ({ ...r, velocity: { min, max } }))}
            />
            <RangePair
              title="4. VRU Constituent Point RCS (dBscm)"
              value={ranges.rcs}
              onChange={(min, max) => setRanges((r) => ({ ...r, rcs: { min, max } }))}
            />
            <RangePair
              title="5. VRU Constituent Point SNR (dB)"
              value={ranges.snr}
              onChange={(min, max) => setRanges((r) => ({ ...r, snr: { min, max } }))}
            />
          </aside>
        </section>

        {/* Bottom-left actions (gradient buttons) */}
        <div className="bottom-actions">
          <button type="button" className="btn btn-apply" style={smallBtn} onClick={applyFilters}>
            Apply
          </button>
          <button type="button" className="btn btn-clear" style={smallBtn} onClick={resetAll}>
            Clear
          </button>
        </div>
      </main>
    </div>
  );
}

/* ---------- Compact range pair (no width cap here) ---------- */
function RangePair({ title, value, onChange }) {
  return (
    <div className="field range-pair" style={{ gap: 4, marginBottom: 8 }}>
      <label style={{ ...smallFont, marginBottom: 2 }}>{title}</label>
      <div className="range-row" style={{ gap: 6 }}>
        <div className="range-box">
          <div className="mini-label" style={{ fontSize: "0.78rem" }}>min</div>
          <SmallInput
            type="number"
            step="1"
            value={value.min}
            onChange={(e) => onChange(e.target.value, value.max)}
          />
        </div>
        <div className="range-box">
          <div className="mini-label" style={{ fontSize: "0.78rem" }}>max</div>
          <SmallInput
            type="number"
            step="1"
            value={value.max}
            onChange={(e) => onChange(value.min, e.target.value)}
          />
        </div>
      </div>
    </div>
  );
}
