// src/components/Header.jsx
import React from "react";
import "./Header.css";
import { FaUserCircle } from "react-icons/fa";
import { NavLink, useNavigate } from "react-router-dom";

const Header = () => {
  const navigate = useNavigate();
  const isAuthed = () => localStorage.getItem("isAuthed") === "true";

  // Intercept "Dataset" clicks: unauth => show Login first, with return path
  const handleDatasetClick = (e) => {
    if (!isAuthed()) {
      e.preventDefault();
      navigate("/login", { state: { from: { pathname: "/dataset" } } });
    }
    // if authed, NavLink proceeds normally to /dataset
  };

  // Header login button always opens Login with a return to /dataset
  const goLoginForDataset = () =>
    navigate("/login", { state: { from: { pathname: "/dataset" } } });

  return (
    <header className="md-header">
      <nav className="md-nav">
        <ul>
          <li className="brand">
            <NavLink to="/" end>
              Micro-Doppler Project
            </NavLink>
          </li>
          <li>
            <NavLink to="/description">Project Description</NavLink>
          </li>
          <li>
            <NavLink to="/research">Research Paper</NavLink>
          </li>
          <li>
            <NavLink to="/contributors">Contributors</NavLink>
          </li>
          <li>
            {/* ✅ Dataset routes through Login first if not authed */}
            <NavLink to="/dataset" onClick={handleDatasetClick}>
              Dataset
            </NavLink>
          </li>
          <li>
            <NavLink to="/feedback">Feedback</NavLink>
          </li>
        </ul>
      </nav>

      {/* ✅ Opens Login, then bounces to /dataset after success */}
      <div className="login-section">
        <button className="login-btn" onClick={goLoginForDataset} aria-label="Login">
          <FaUserCircle className="login-icon" />
          Login
        </button>
      </div>
    </header>
  );
};

export default Header;
